abstract class MonthByMonthQuantity implements QuantityOfInterest {

    private final double[] values;

    protected MonthByMonthQuantity(final double[] values) {
        this.values = values;
    }

    @Override
    public double valueAt(final int time) {
        return values[time - 1];
    }
}

PolynomialQuantity.java:
abstract class PolynomialQuantity implements QuantityOfInterest {

    private final double[] coefficients;

    protected PolynomialQuantity(final double[] coefficients) {
        this.coefficients = coefficients;
    }

    @Override
    public double valueAt(final int time) {
        double value = 0.0;
        for(int i = 0; i < coefficients.length; i++) {
            value += coefficients[i] * Math.pow(time, i);
        }
        return value;
    }
}

